import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './App.css';
import logo from './assets/logo.png';
import mobileDuo from './assets/mobile_duo1.png';

function App() {
  const section2Ref = useRef(null);

  // Track scroll progress for Section 2
  const { scrollYProgress } = useScroll({
    target: section2Ref,
    offset: ["start end", "center center"], // Start and center alignment for smooth animation
  });

  // Transformations for the mobile image in Section 2
  const phoneImageY = useTransform(scrollYProgress, [0, 1], ["100%", "0%"]);
  const phoneImageOpacity = useTransform(scrollYProgress, [0, 0.5], [0, 1]);

  return (
    <div className="app">
      {/* Section 1 */}
      <section className="hero-section">
        <div className="header">
          <img src={logo} alt="Pigeon Post Logo" className="logo" />
          <button className="demo-button">Request Demo</button>
        </div>
        <div className="hero-content">
          <motion.div
            className="hero-text-container"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
          >
            <h1 className="hero-title">
              Creativity
              <br />
              <span className="hero-subtitle">Beyond Limits</span>
            </h1>
            <p className="hero-description">
              Pigeon Post is built for boundless. For creators who want to break
              through barriers.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Section 2 */}
      <section className="platform-section" ref={section2Ref}>
        <motion.div
          className="image-container"
          style={{
            y: phoneImageY,
            opacity: phoneImageOpacity,
          }}
        >
          <img src={mobileDuo} alt="Mobile Duo" className="mobile-image" />
        </motion.div>
        <div className="text-container">
          <h1>
            Platform of <span>Possibilities</span>
          </h1>
          <p>Everything you need to create, connect, and thrive.</p>
        </div>
      </section>
    </div>
  );
}

export default App;
