import React from 'react';
import { motion } from 'framer-motion';
import './App.css';
import logo from './assets/logo.png';
import leaf from './assets/leaf.png';

function App() {
  return (
    <div className="app">
      {/* Single Section */}
      <section className="hero-section">
        <div className="header">
          <img src={logo} alt="Pigeon Post Logo" className="logo" />
          <button className="demo-button">Request Demo</button>
        </div>
        <div className="hero-content">
          <div className="hero-leaf-container">
            <img src={leaf} alt="Leaf" className="hero-leaf" />
            <motion.h1
              className="hero-title"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 1 }}
              viewport={{ once: true }}
            >
              Creativity
              <br />
              <motion.span
                className="hero-subtitle"
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 1, delay: 0.2 }}
                viewport={{ once: true }}
              >
                Beyond Limits
              </motion.span>
            </motion.h1>
          </div>
        </div>
        <motion.p
          className="hero-description"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.4 }}
          viewport={{ once: true }}
        >
          Pigeon Post is built for boundless. For creators who want to break through barriers
        </motion.p>
      </section>
    </div>
  );
}

export default App;
