import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './App.css';
import logo from './assets/logo.png';
import leaf from './assets/leaf.png';
import mobileDuo from './assets/mobile_duo1.png';

function App() {
  const handleRequestDemoClick = () => {
    console.log("Request Demo clicked!");
  };

  // Reference for the hero section
  const heroRef = useRef(null);
  
  // Track scroll progress within the hero section
  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"]
  });

  // Map scroll progress to mobile image animation:
  // As you scroll from 10% to 30% of the hero section,
  // the mobile image moves from 100px down (y: 100) to its final position (y: 0)
  // and its opacity goes from 0 to 1.
  const mobileImageY = useTransform(scrollYProgress, [0.1, 0.3], [100, 0]);
  const mobileImageOpacity = useTransform(scrollYProgress, [0.1, 0.3], [0, 1]);

  // Fade out the text container as you scroll down (from opacity 1 to 0)
  const textOpacity = useTransform(scrollYProgress, [0.1, 0.3], [1, 0]);

  return (
    <div className="app">
      <section className="hero-section" ref={heroRef}>
        <div className="header">
          <img src={logo} alt="Pigeon Post Logo" className="logo" />
          <button className="demo-button" onClick={handleRequestDemoClick}>
            Request Demo
          </button>
        </div>

        <div className="hero-content">
          {/* Text container that fades out as you scroll */}
          <motion.div
            className="hero-leaf-container"
            style={{ opacity: textOpacity }}
          >
            <img src={leaf} alt="Leaf" className="hero-leaf" />
            <h1 className="hero-title">
              Creativity
              <br />
              <span className="hero-subtitle">Beyond Limits</span>
            </h1>
          </motion.div>

          {/* Mobile image that animates into view based on scroll */}
          <motion.img
            src={mobileDuo}
            alt="Mobile Duo"
            className="mobile-image"
            style={{
              position: 'absolute',
              left: '-10%', // Adjusted to the left
              top: '-20%',  // Adjusted upward
              transform: 'translate(-50%, -50%)',
              zIndex: 2,
              y: mobileImageY,
              opacity: mobileImageOpacity,
            }}
          />
        </div>
      </section>
    </div>
  );
}

export default App;
