import React from 'react';
import { motion } from 'framer-motion';
import './App.css';
import logo from './assets/logo.png';
import mobileDuo from './assets/mobile_duo1.png';

function App() {
  const handleRequestDemoClick = () => {
    console.log("Request Demo clicked!");
  };

  return (
    <div className="app">
      <section className="hero-section">
        <div className="header">
          <img src={logo} alt="Pigeon Post Logo" className="logo" />
          <button className="demo-button" onClick={handleRequestDemoClick}>
            Request Demo
          </button>
        </div>

        <div className="hero-content">
          {/* Text container using the provided structure */}
          <div className="creativity-beyond-limits-container">
            <p className="creativity">
              <span>Creativity</span>
              <span className="span">{` `}</span>
            </p>
            <p className="beyond-limits">Beyond Limits</p>
          </div>

          {/* Animated Mobile Image */}
          <motion.img
            src={mobileDuo}
            alt="Mobile Duo"
            className="mobile-image"
            initial={{ y: 150, opacity: 0 }} // Starts off-screen below and hidden
            animate={{ y: 0, opacity: 1 }}    // Animates upward to its final position
            transition={{ delay: 5, duration: 1 }}
            style={{
              position: 'absolute',
              left: '45%',  // Shift a little left
              top: '40%',   // Shift a little higher
              transform: 'translate(-50%, -50%)',
              zIndex: 2,
            }}
          />
        </div>
      </section>
    </div>
  );
}

export default App;
