import React, { useRef, useEffect, useState } from 'react';
import './App.css';
import logo from './assets/logo.png';
import leaf from './assets/leaf.png';
import mobileDuo from './assets/mobile_duo1.png';

function App() {
  const imageRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsVisible(entry.isIntersecting);
      },
      { threshold: 0.5 }
    );

    if (imageRef.current) {
      observer.observe(imageRef.current);
    }

    return () => {
      if (imageRef.current) observer.unobserve(imageRef.current);
    };
  }, []);

  return (
    <div className="app">
      {/* First Five Sections (existing content) */}
      {/* ... */}

      {/* Sixth Section */}
      <section className="next-gen-section">
        <div className="text-container">
          <p className="subtitle">A place where creators write the future, together;</p>
          <h1 className="main-title">Welcome to the next generation of creativity</h1>
        </div>
        <div
          className={`image-container ${isVisible ? 'animate' : ''}`}
          ref={imageRef}
        >
          <img src={mobileDuo} alt="Mobile Duo" className="mobile-image" />
        </div>
      </section>
    </div>
  );
}

export default App;
