import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './App.css';
import logo from './assets/logo.png';
import leaf from './assets/leaf.png';
import mobileDuo from './assets/mobile_duo1.png';

function App() {
  // Create a ref for the hero section
  const section1Ref = useRef(null);

  // Use the scroll progress within Section 1
  const { scrollYProgress } = useScroll({
    target: section1Ref,
    offset: ["start end", "end start"]
  });

  // When scrolling from the start to about 30% of Section 1,
  // animate the image from 50px below to its final position (y: 0)
  // and fade it in from opacity 0 to 1.
  const imageY = useTransform(scrollYProgress, [0, 0.3], [50, 0]);
  const imageOpacity = useTransform(scrollYProgress, [0, 0.3], [0, 1]);

  const handleRequestDemoClick = () => {
    console.log("Request Demo clicked!");
  };

  return (
    <div className="app">
      {/* Hero Section (Section 1) */}
      <section className="hero-section" ref={section1Ref}>
        <div className="header">
          <img src={logo} alt="Pigeon Post Logo" className="logo" />
          <button className="demo-button" onClick={handleRequestDemoClick}>
            Request Demo
          </button>
        </div>

        {/* The container for the main hero content */}
        <div className="hero-content">
          <motion.div
            className="hero-leaf-container"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: false }}
            transition={{ duration: 1 }}
          >
            <img src={leaf} alt="Leaf" className="hero-leaf" />
            <h1 className="hero-title">
              Creativity
              <br />
              <span className="hero-subtitle">Beyond Limits</span>
            </h1>
          </motion.div>

          {/* Mobile image that animates into view on scroll */}
          <motion.img
            src={mobileDuo}
            alt="Mobile Duo"
            className="mobile-image"
            style={{
              position: 'absolute',
              left: '50%',
              top: '50%', // Adjust this value if needed so the image overlaps the text as desired
              transform: 'translate(-50%, -50%)',
              zIndex: 2,
              y: imageY,
              opacity: imageOpacity,
            }}
          />
        </div>
      </section>
    </div>
  );
}

export default App;
