import React, { useRef } from 'react';
import { motion, useInView } from 'framer-motion';
import './App.css';
import logo from './assets/logo.png';
import leaf from './assets/leaf.png';
import mobileDuo from './assets/mobile_duo1.png';

function App() {
  const ref = useRef(null);
  const isInView = useInView(ref, { threshold: 0.5 });

  return (
    <div className="app">
      {/* First Section */}
      <section className="hero-section">
        <div className="header">
          <img src={logo} alt="Logo" className="logo" />
          <button className="demo-button">Request Demo</button>
        </div>
        <div className="content">
          <h1>
            Creativity
            <br />
            <span>Beyond Limits</span>
          </h1>
        </div>
        <img src={leaf} alt="Background Leaf" className="background-leaf" />
      </section>

      {/* Second Section */}
      <section className="info-section">
        <p className="info-text">
          Pigeon Post is built for boundless. For creators who want to break through barriers
        </p>
      </section>

      {/* Third Section */}
      <section className="platform-section">
        <h1 className="platform-heading">
          One Platform
          <br />
          <span>Infinite Potential</span>
        </h1>
      </section>

      {/* Fourth Section */}
      <section className="create-section">
        <p className="create-text">
          Everything you need, together in one place. No friction, no barriersâ€”just the space to <span className="highlight">create</span> your best work.
        </p>
      </section>

      {/* Fifth Section */}
      <section className="new-era-section">
        <h1 className="new-era-heading">
          <span className="small-text">Not Just a Platform</span>
          <br />
          A New Era
        </h1>
      </section>

      {/* Sixth Section */}
      <section className="next-gen-section">
        <div className="text-container">
          <p className="subtitle">A place where creators write the future, together;</p>
          <h1 className="main-title">Welcome to the next generation of creativity</h1>
        </div>
        <motion.div
          className="image-container"
          ref={ref}
          initial={{ opacity: 0, y: 100 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 100 }}
          transition={{ duration: 0.8, ease: 'easeOut' }}
        >
          <img src={mobileDuo} alt="Mobile Duo" className="mobile-image" />
        </motion.div>
      </section>
    </div>
  );
}

export default App;
