import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './App.css';
import logo from './assets/logo.png';
import leaf from './assets/leaf.png';
import mobileDuo from './assets/mobile_duo1.png';

function App() {
  // Reference for the hero section to track scroll within it
  const heroRef = useRef(null);

  // useScroll hook to track scroll progress of the hero section
  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"]
  });

  // Map scroll progress (from 0 to 0.2) to mobile image's vertical movement and opacity
  const mobileImageY = useTransform(scrollYProgress, [0, 0.2], [100, 0]);
  const mobileImageOpacity = useTransform(scrollYProgress, [0, 0.2], [0, 1]);

  // Map scroll progress (from 0 to 0.2) to text opacity (fades out)
  const textOpacity = useTransform(scrollYProgress, [0, 0.2], [1, 0]);

  const handleRequestDemoClick = () => {
    console.log("Request Demo clicked!");
  };

  return (
    <div className="app">
      <section className="hero-section" ref={heroRef}>
        <div className="header">
          <img src={logo} alt="Pigeon Post Logo" className="logo" />
          <button className="demo-button" onClick={handleRequestDemoClick}>
            Request Demo
          </button>
        </div>

        <div className="hero-content">
          {/* Text container fades out as you scroll down */}
          <motion.div
            className="hero-leaf-container"
            style={{ opacity: textOpacity }}
          >
            <img src={leaf} alt="Leaf" className="hero-leaf" />
            <h1 className="hero-title">
              Creativity
              <br />
              <span className="hero-subtitle">Beyond Limits</span>
            </h1>
          </motion.div>

          {/* Mobile image animates based on scroll progress */}
          <motion.img
            src={mobileDuo}
            alt="Mobile Duo"
            className="mobile-image"
            style={{
              position: 'absolute',
              left: '-10%',       // Moved further to the left
              top: '-20%',        // Moved further up
              transform: 'translate(-50%, -50%)',
              zIndex: 2,
              y: mobileImageY,
              opacity: mobileImageOpacity,
            }}
          />
        </div>
      </section>

      {/* Extra content to enable scrolling */}
      <div style={{ height: "150vh" }} />
    </div>
  );
}

export default App;
